import React, { useEffect, useState } from 'react';
import { Button, Grid2, Container, Box, Typography, useTheme, Paper } from '@mui/material';
import { spotifyAuthFlow, hashSpotifyToken } from '../api_utils/spotifyUtils.js';
import { hashAppleToken } from '../api_utils/appleUtils.js';
import useAuth from '../context.js';
import CheckIcon from '@mui/icons-material/Check';
export default function Start() {
    const theme = useTheme();
    const { auth, setAuth } = useAuth()

    const setRecent = (recent) => {
        setAuth((prev) => {
            return {
                ...prev,
                recent: recent
            }
        })
    }
    const handleSpotifyButtonClick = () => {
        window.sessionStorage.setItem("recent", 'spotify');
        window.location.href = spotifyAuthFlow();
    }
    console.log(auth.spotifyIsAuth)

    useEffect(() => {
        const recent = window.sessionStorage.getItem("recent")
        if (recent === 'spotify') {
            hashSpotifyToken();
        } else if (recent === 'apple') {
            hashAppleToken();
        }
        const spotifyToken = window.sessionStorage.getItem("spotify_token");
        const appleToken = window.sessionStorage.getItem("apple_token");
        if (spotifyToken) {
            setAuth((prev) => {
                return {
                    ...prev,
                    spotifyIsAuth: true
                }
            })
        }
        if (appleToken) {
            setAuth((prev) => {
                return {
                    ...prev,
                    appleIsAuth: true
                }
            })
        }
        console.log(auth)
    }, [])


    return (

        <Box
        sx={{
          height: "100vh", // Full viewport height
          display: "flex",
          flexDirection: "column",
          justifyContent: "center", // Centers content vertically
          alignItems: "center", // Centers content horizontally
        }}
      >
        <Box
          sx={{
            display: "flex",
            justifyContent: "space-around",
            alignItems: "center",
            width: "80%", // Adjust width as needed
            gap: 4,
          }}
        >
          {/* Spotify Box */}
          <Paper
            elevation={8}
            sx={{
              backgroundColor: theme.palette.background.paper,
              padding: 5,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              width: "300px",
            }}
          >
            <Typography variant="h2">Spotify</Typography>
            {auth.spotifyIsAuth ? (
              <CheckIcon />
            ) : (
              <Button variant="contained" onClick={handleSpotifyButtonClick}>
                Contained
              </Button>
            )}
          </Paper>

          {/* Apple Box */}
          <Paper
            elevation={8}
            sx={{
              backgroundColor: theme.palette.background.paper,
              padding: 5,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              width: "300px",
            }}
          >
            <Typography variant="h2">Apple</Typography>
            <Button variant="contained">Contained</Button>
          </Paper>
        </Box>
      </Box>

    )
}
