import React, { useEffect, useState } from 'react';
import { Button, Grid2, Container, Box, Typography, useTheme, Paper } from '@mui/material';
import { spotifyAuthFlow, hashSpotifyToken } from '../api_utils/spotifyUtils.js';
import { hashAppleToken } from '../api_utils/appleUtils.js';
import useAuth from '../context.js';
import CheckIcon from '@mui/icons-material/Check';
export default function Start() {
    const theme = useTheme();
    const { auth, setAuth } = useAuth()

    const setRecent = (recent) => {
        setAuth((prev) => {
            return {
                ...prev,
                recent: recent
            }
        })
    }
    const handleSpotifyButtonClick = () => {
        window.sessionStorage.setItem("recent", 'spotify');
        window.location.href = spotifyAuthFlow();
    }
    console.log(auth.spotifyIsAuth)

    useEffect(() => {
        const recent = window.sessionStorage.getItem("recent")
        if (recent === 'spotify') {
            hashSpotifyToken();
        } else if (recent === 'apple') {
            hashAppleToken();
        }
        const spotifyToken = window.sessionStorage.getItem("spotify_token");
        const appleToken = window.sessionStorage.getItem("apple_token");
        if (spotifyToken) {
            setAuth((prev) => {
                return {
                    ...prev,
                    spotifyIsAuth: true
                }
            })
        }
        if (appleToken) {
            setAuth((prev) => {
                return {
                    ...prev,
                    appleIsAuth: true
                }
            })
        }
        console.log(auth)
    }, [])


    return (

        <Container sx={{ height: '100%' }}>
            <Grid2 container justifyContent="center" alignItems="center">
                <Grid2 size={6} container justifyContent="center" alignItems="center">
                    <Box justifyContent="center" alignItems="center" sx={{ m: 30 }}>
                        <Paper elevation={8} sx={{
                            backgroundColor: theme.palette.background.paper, padding: 5, display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center",
                        }} >
                            <Typography variant='h2'>Spotify</Typography>
                            {auth.spotifyIsAuth ?
                                <CheckIcon />
                                :
                                <Button variant="contained" onClick={handleSpotifyButtonClick}>Contained</Button>}
                        </Paper>
                    </Box>
                </Grid2>
                <Grid2 size={6} container justifyContent="center" alignItems="center">
                    <Box justifyContent="center" alignItems="center" sx={{ m: 30 }}>
                        <Paper elevation={8} sx={{ backgroundColor: theme.palette.background.paper, padding: 5, display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center"}} >
                            <Typography justifyContent="center" alignItems="center" variant='h2' sx={{ width: "fit-content" }}>Apple</Typography>
                            <Button variant="contained">Contained</Button>
                        </Paper>
                    </Box>
                </Grid2>
            </Grid2>
        </Container >

    )
}
