import React from 'react';
import { Box, Paper, Typography, List, ListItem, ListItemText } from '@mui/material';
import useAppContext from '../context';
import theme from '../styles/theme';

export default function PlaylistList({ provider }) {
    const { state, updateState } = useAppContext();
    const playlists = state.playlists[provider].playlists;
    const selectedPlaylist = state.playlists[provider].selectedPlaylist;
    const handlePlaylistClick = (playlist) => {
        const newObj = state.playlists;
        newObj[provider] = {
            playlists: playlists,
            selectedPlaylist: null
        }
        updateState('playlists', newObj)
        setTimeout(() => {
            newObj[provider] = {
                playlists: playlists,
                selectedPlaylist: playlist
            }
            updateState('playlists', newObj)
        }, 10)

    };
    const selectedBorder = (playlist) => {
        if (selectedPlaylist === playlist){
            return `3px solid ${theme.palette.primary.main}`
        }else {
            return "none"
        }
    };

    const handleResize = () => {
        if(selectedPlaylist){
            return "20%"
        }else{
            return "50%"
        }
    }
    return (<Box sx={{boxSizing: "border-box", width: ()=>handleResize(), minWidth:"150px", height: "100%", backgroundColor: theme.palette.background.paper, padding: "10px", shadow: theme.shadows[7]}}>

            <Typography variant="h3" sx={{ marginBottom: 2 }}>
                Playlists
            </Typography>
            <List sx={{display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "flex-start" }}>

                {playlists.map((playlist, index) => (
                    <ListItem
                        key={index}
                        button
                        onClick={() => handlePlaylistClick(playlist)}
                        selected={true}
                        sx={{
                            padding: "4px",
                            margin: "3px",
                            borderRadius: "8px",
                            border: selectedBorder(playlist),
                            backgroundColor: theme.palette.background.default,
                            '&.Mui-selected': {
                                bgcolor: 'grey.200',
                                fontWeight: 'bold',
                                border: '2px solid red', // For visibility
                                borderRadius: '8px',
                            },
                            '&.Mui-selected:hover': { bgcolor: 'grey.300' },
                        }}
                    >
                        <ListItemText primary={playlist.title} />
                    </ListItem>
                ))}
            </List>

    </Box>)
}
