import React, { useEffect, useState } from 'react';
import { Button, Grid2, Container, Box, Typography, useTheme, Paper } from '@mui/material';
import { spotifyAuthFlow, hashSpotifyToken } from '../api_utils/spotifyUtils.js';
import { hashAppleToken, mockAppleAuth } from '../api_utils/appleUtils.js';
import useAuth from '../context.js';
import CheckIcon from '@mui/icons-material/Check';
export default function Start() {
    const theme = useTheme();
    const { auth, setAuth } = useAuth()

    const setRecent = (recent) => {
        setAuth((prev) => {
            return {
                ...prev,
                recent: recent
            }
        })
    }
    const handleSpotifyButtonClick = () => {
        window.sessionStorage.setItem("recent", 'spotify');
        window.location.href = spotifyAuthFlow();
    }
    const handleAppleButtonClick = () => {
        window.sessionStorage.setItem("recent", 'apple');
        mockAppleAuth()
        setAuth((prev) => {
            return {
                ...prev,
                appleIsAuth: true
            }
        })
    }


    useEffect(() => {
        const recent = window.sessionStorage.getItem("recent")
        if (recent === 'spotify') {
            hashSpotifyToken();
        } else if (recent === 'apple') {
            hashAppleToken();
        }
        const spotifyToken = window.sessionStorage.getItem("spotify_token");
        const appleToken = window.sessionStorage.getItem("apple_token");
        if (spotifyToken) {
            setAuth((prev) => {
                return {
                    ...prev,
                    spotifyIsAuth: true
                }
            })
        }
        if (appleToken) {
            setAuth((prev) => {
                return {
                    ...prev,
                    appleIsAuth: true
                }
            })
        }
        console.log(auth)
    }, [])

   const buttonIsDisabled = !auth.appleIsAuth || !auth.spotifyIsAuth

    return (

        <Box
            sx={{
                height: "100vh", // Full viewport height
                display: "flex",
                flexDirection: "column",
                justifyContent: "center", // Centers content vertically
                alignItems: "center", // Centers content horizontally
            }}
        >
            <Box
                sx={{
                    display: "flex",
                    justifyContent: "space-around",
                    alignItems: "center",
                    width: "80%", // Adjust width as needed
                    gap: 4,
                }}
            >
                {/* Spotify Box */}
                <Paper
                    elevation={8}
                    sx={{
                        backgroundColor: theme.palette.background.paper,
                        padding: 5,
                        display: "flex",
                        flexDirection: "column",
                        alignItems: "center",
                        justifyContent: "center",
                        width: "400px",
                        height: "200px",
                        marginTop: -25
                    }}
                >
                    <Typography variant="h2" sx={{ marginBottom: 5 }}>Spotify</Typography>
                    {auth.spotifyIsAuth ? (
                        <CheckIcon />
                    ) : (
                        <Button variant="contained" onClick={handleSpotifyButtonClick}>
                            Contained
                        </Button>
                    )}
                </Paper>

                {/* Apple Box */}
                <Paper
                    elevation={8}
                    sx={{
                        backgroundColor: theme.palette.background.paper,
                        padding: 5,
                        display: "flex",
                        flexDirection: "column",
                        alignItems: "center",
                        justifyContent: "center",
                        width: "400px",
                        height: "200px",
                        marginTop: -25
                    }}
                >
                    <Typography variant="h2" sx={{ marginBottom: 5 }} onClick={handleAppleButtonClick}>Apple</Typography>
                    {
                        auth.appleIsAuth ?
                            <CheckIcon />
                            :
                            <Button variant="contained" >Contained</Button>
                    }
                </Paper>
                <Button
                    variant="contained"
                    sx={{
                        width: "300px",
                        fontSize: "20px",
                        position: "absolute",
                        top: "65%",
                        left: "50%",
                        transform: "translate(-50%, -50%)",
                    }}
                    disabled={buttonIsActive}
                >
                    Continue
                </Button>
            </Box>
        </Box>

    )
}
